import React, { useRef, useState } from 'react';
import './CardContainer.scss';

function CardContainer({ items, openModal }) {
  const [offset, setOffset] = useState(0);
  const cardContainerRef = useRef();

  const cardWidth = 300; // Replace with the actual width of your cards (including margins)

  const scrollLeft = () => {
    setOffset(Math.max(offset - cardWidth, 0));
  };

  const scrollRight = () => {
    setOffset(Math.min(offset + cardWidth, (items.length - 3) * cardWidth));
  };

  // Scroll the container whenever the offset changes
  useEffect(() => {
    cardContainerRef.current.scrollTo({
      left: offset,
      behavior: 'smooth'
    });
  }, [offset]);

  return (
    <div className="card-container-outer">
      <button onClick={scrollLeft}>Left</button>
      <div className="card-container-inner" ref={cardContainerRef}>
        {items.map(item => (
          <Card key={item.id} item={item} openModal={openModal} />
        ))}
      </div>
      <button onClick={scrollRight}>Right</button>
    </div>
  );
}

export default CardContainer;
