// Card.jsx
import React, { useState } from "react";
import './Card.scss';
import Modal from '../modal/Modal';

function Card({ item }) {
    const [isModalOpen, setIsModalOpen] = useState(false);

    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    // Check if item and item.description are defined
    let cardDescription = item?.description || '';
    if (item?.description) {
        // Split the description into words
        const words = item.description.split(' ');

        // Get the first 50 words for the card
        cardDescription = words.slice(0, 25).join(' ');

        // Add '...' to the end if there are more than 50 words
        if (words.length > 25) {
            cardDescription += '...<br /><strong>Click More Info for the full description.</strong>';
        }
    }

    return (
        <div className="card">
            <img className="card-img" src={item.img} alt={item.title} />
            <h1 className="card-title">{item.title}</h1>
            <h3 className="card-description" dangerouslySetInnerHTML={{ __html: cardDescription }}></h3>
            <div class="card-divider">
            <p className="card-tech">{item.tech}</p>
            </div>
            <div className="button-container">
                <a className="card-button" href={item.url}>Github</a>
                <button className="card-button" onClick={openModal}>More Info</button>
            </div>
            {isModalOpen && <Modal item={item} closeModal={closeModal} />}
        </div>
    );
}

export default Card;
