import React, { useState } from "react";
import './Card.scss';
import Modal from '../modal/Modal'; // Assuming you have a Modal component

function Card({ item }) {
    const [isModalOpen, setIsModalOpen] = useState(false);

    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    return (
        <div className="card">
            <img className="card-img" src={item.img} alt={item.title} />
            <h1 className="card-title">{item.title}</h1>
            <h3 className="card-description">{item.description}</h3>
            <h3 className="card-tech">{item.tech}</h3>
            <div className="button-container">
                <a className="card-button" href={item.url}>Github</a>
                <button className="card-button" onClick={openModal}>More Info</button>
            </div>
            {isModalOpen && <Modal item={item} closeModal={closeModal} />}
        </div>
    );
}

export default Card;
