// Card.jsx
import React, { useState } from "react";
import './Card.scss';
import Modal from '../modal/Modal';

function Card({ item }) {
    const [isModalOpen, setIsModalOpen] = useState(false);

    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    // Check if item and item.description are defined
    let cardDescription = item?.description || '';
    let modalDescription = '';
    if (item?.description) {
        // Split the description into lines
        const lines = item.description.split('\n');

        // Check if there are more than 6 lines
        if (lines.length > 6) {
            // Get the first 6 lines for the card
            cardDescription = lines.slice(0, 6).join('\n');

            // Get the rest of the lines for the modal
            modalDescription = lines.slice(6).join('\n');
        }
    }

    // Create a new item object for the Modal
    const modalItem = { ...item, description: modalDescription };

    return (
        <div className="card">
            <img className="card-img" src={item.img} alt={item.title} />
            <h1 className="card-title">{item.title}</h1>
            <h3 className="card-description">{cardDescription}</h3>
            <div className="button-container">
                <a className="card-button" href={item.url}>Github</a>
                <button className="card-button" onClick={openModal}>More Info</button>
            </div>
            {isModalOpen && <Modal item={modalItem} closeModal={closeModal} />}
        </div>
    );
}

export default Card;
