// Card.jsx
import React, { useState } from "react";
import './Card.scss';
import Modal from '../modal/Modal';

function Card({ item }) {
    const [isModalOpen, setIsModalOpen] = useState(false);

    const openModal = () => {
        setIsModalOpen(true);
    };

    const closeModal = () => {
        setIsModalOpen(false);
    };

    // Split the description into lines
    const lines = item.description.split('\n');

    // Get the first 6 lines for the card
    const cardDescription = lines.slice(0, 6).join('\n');

    // Get the rest of the lines for the modal
    const modalDescription = lines.slice(6).join('\n');

    return (
        <div className="card">
            <img className="card-img" src={item.img} alt={item.title} />
            <h1 className="card-title">{item.title}</h1>
            <h3 className="card-description">{cardDescription}</h3>
            <div className="button-container">
                <a className="card-button" href={item.url}>Github</a>
                <button className="card-button" onClick={openModal}>More Info</button>
            </div>
            {isModalOpen && <Modal item={{...item, description: modalDescription}} closeModal={closeModal} />}
        </div>
    );
}

export default Card;
